function EquipWeapon(B)
    pcall(
        function()
            if game.Players.LocalPlayer.Backpack:FindFirstChild(B) then
                local C = game.Players.LocalPlayer.Backpack:FindFirstChild(B)
                game.Players.LocalPlayer.Character.Humanoid:EquipTool(C)
            end
        end
    )
end
function UnEquipWeapon(D)
    if game.Players.LocalPlayer.Character:FindFirstChild(D) then
        _G.NotAutoEquip = true
        wait(.5)
        game.Players.LocalPlayer.Character:FindFirstChild(D).Parent = game.Players.LocalPlayer.Backpack
        wait(.1)
        _G.NotAutoEquip = false
    end
end
function EquipBloxFruit()
    for E, F in pairs(game.Players.LocalPlayer.Backpack:GetChildren()) do
        if F.ToolTip == "Blox Fruit" then
            if game.Players.LocalPlayer.Backpack:FindFirstChild(tostring(F.Name)) then
                EquipWeapon(F.Name)
            end
        end
    end
end
function EquipWeaponSword()
    pcall(
        function()
            for E, F in pairs(game.Players.LocalPlayer.Backpack:GetChildren()) do
